var displayjs = {
  bindToElem: function(elem, index, data) {
    document.getElementsByTagName(elem)[index].innerHTML = data;
  },
  bindToId: function(id, data) {
    document.getElementById(id).innerHTML = data;
  },
  bindToElems: function(elem, data) {
    var elems = document.getElementsByTagName(elem);
    for (var i = 0; i < elems.length; i++) {
      elems[i].innerHTML = data;
    }
  },
  bindByVar: function(_var, index, data, isArray) {
    if (isArray === true) {
      if (index == undefined) {index = 0;}
      _var[index].innerHTML = data;
    } else if (isArray === false) {
      _var.innerHTML = data;
    } else {
      throw "Type of argument 'isArray' should be a boolean but it is a " + typeof isArray;
    }
  }
};
