// Functions
var brunoJS = {
  render: function(compo, method) {
    /*
      We use eval because 'new compo()' will just create
      a new object named 'compo'. If we use eval, and the 
      value of 'compo' is 'Car', we will get 'new Car()'.
    */
    var compon = eval("new " + compo + "()");
    method.innerHTML = compon.render();
  },
  addEvent: function(method = this, type = "click", func = function() {}) {
    if (method.addEventListener) {
      method.addEventListener(type, func);
    } else if (method.attachEvent) {
      method.attachEvent("on" + type, func)
    }
  }
}
