function BrunoFunc(parms, body) {
  var txt, counter = "";
  for (counter = 0; counter < parms.length; counter++) {
    txt += parms[counter];
    if (parms.length > counter) txt += ", ";
  }
  return eval("function(" + txt + ") {" + body + "}") // We use eval to use an array to make function arguments. 
}
