function Lambda(arr, run) {
  if (
    typeof arr == "object" &&
    arr instanceof Array == true
  ) {
    return eval("function(" + arr.toString() + ") {" + run + "}");
  } else {
    throw "Parameter 'arr' must be a array"; 
    return;
  }
}
